import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import Stack from '@mui/material/Stack';

import Card from '../components/card';
import FormGroup from '../components/form-group';

import { mensagemSucesso, mensagemErro } from '../components/toastr';

import '../custom.css';

import axios from 'axios';
import { BASE_URL } from '../config/axios';

function CadastroPedido() {
  const { idParam } = useParams();

  const navigate = useNavigate();

  const baseURL = `${BASE_URL}/pedidos`;

  const [id, setId] = useState('');
    const [cliente, setCliente] = useState({});
    const [produtos, setProdutos] = useState([]);
    const [status, setStatus] = useState('');


  const [dados, setDados] = React.useState([]);

  function inicializar() {
  if (idParam == null) {
    setId('');
    setCliente('');
    setProdutos('');
    setStatus('');
  } else {
    setId(dados.id);
    setNome(dados.nome);
    setCpf(dados.cpf);
    setEmail(dados.email);
    setSenha(dados.senha);
    setLogradouro(dados.logradouro);
    setNumero(dados.numero);
    setComplemento(dados.complemento);
    setBairro(dados.bairro);
    setCidade(dados.cidade);
    setUf(dados.uf);
    setCep(dados.cep);
  }
}


  async function salvar() {
    let data = { id, nome, cpf, email, senha };
    data = JSON.stringify(data);
    if (idParam == null) {
      await axios
        .post(baseURL, data, {
          headers: { 'Content-Type': 'application/json' },
        })
        .then(function (response) {
          mensagemSucesso(`Pedido ${nome} cadastrado com sucesso!`);
          navigate(`/listagem-pedidos`);
        })
        .catch(function (error) {
          mensagemErro(error.response.data);
        });
    } else {
      await axios
        .put(`${baseURL}/${idParam}`, data, {
          headers: { 'Content-Type': 'application/json' },
        })
        .then(function (response) {
          mensagemSucesso(`Pedido ${nome} alterado com sucesso!`);
          navigate(`/listagem-pedidos`);
        })
      .catch(function (error) {
        if (error.response) {
            mensagemErro(error.response.data);
        } else {
            mensagemErro('Ocorreu um erro de rede.');
  }
});
 
    }
  }

  async function buscar() {
    await axios.get(`${baseURL}/${idParam}`).then((response) => {
      setDados(response.data);
    });
    setId(dados.id);
    setNome(dados.nome);
    setCpf(dados.cpf);
    setEmail(dados.email);
    setSenha(dados.senha);
    setLogradouro(dados.logradouro);
    setNumero(dados.numero);
    setComplemento(dados.complemento);
    setBairro(dados.bairro);
    setCidade(dados.cidade);
    setUf(dados.uf);
    setCep(dados.cep);
  }

  useEffect(() => {
    buscar(); // eslint-disable-next-line
  }, [id]);

  if (!dados) return null;

  return (
    <div className='container'>
      <Card title='Cadastro de Pedido'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
                
              <Stack spacing={1} padding={1} direction='row'>
                <button
                  onClick={salvar}
                  type='button'
                  className='btn btn-success'
                >
                  Salvar
                </button>
                <button
                  onClick={inicializar}
                  type='button'
                  className='btn btn-danger'
                >
                  Cancelar
                </button>
              </Stack>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default CadastroPedido;
