import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Stack from '@mui/material/Stack';
import { IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import Card from '../components/card';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import { BASE_URL } from '../config/axios';

const baseURL = `${BASE_URL}/pedidos`;

function ListagemPedidos() {
  const navigate = useNavigate();
  const [pedidos, setPedidos] = useState([]);

  const editar = (id) => {
    navigate(`/editar-pedido/${id}`);
  };

  const excluir = async (id) => {
    try {
      await axios.delete(`${baseURL}/${id}`);
      mensagemSucesso('Pedido excluído com sucesso!');
      setPedidos(pedidos.filter((pedido) => pedido.id !== id));
    } catch (error) {
      mensagemErro('Erro ao excluir o pedido');
    }
  };

  useEffect(() => {
    axios.get(baseURL).then((response) => {
      setPedidos(response.data);
    });
  }, []);

  return (
    <div className='container'>
      <Card title='Listagem de Pedidos'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <table className='table table-hover'>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Cliente</th>
                    <th>Status</th>
                    <th>Preço Total</th>
                    <th>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {pedidos.map((pedido) => (
                    <tr key={pedido.id}>
                      <td>{pedido.id}</td>
                      <td>{pedido.cliente.nome}</td>
                      <td>{pedido.status}</td>
                      <td>{pedido.precoTotal}</td>
                      <td>
                        <Stack spacing={1} padding={0} direction='row'>
                          <IconButton
                            aria-label='edit'
                            onClick={() => editar(pedido.id)}
                          >
                            <EditIcon />
                          </IconButton>
                          <IconButton
                            aria-label='delete'
                            onClick={() => excluir(pedido.id)}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Stack>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default ListagemPedidos;
