import React, { useState, useEffect } from 'react';
import Card from '../components/card';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import '../custom.css';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

const produtosURL = `${BASE_URL}/produtos`;
const carrinhosURL = `${BASE_URL}/carrinhos`;

function ListagemEstoque() {
  const [produtos, setProdutos] = useState([]);
  const [quantidades, setQuantidades] = useState({});

  useEffect(() => {
    axios.get(produtosURL).then((response) => {
      setProdutos(response.data);
    });
  }, []);

  const atualizarQuantidade = async (produtoId, quantidade) => {
    try {
      await axios.put(`${produtosURL}/${produtoId}`, { quantidade });
      mensagemSucesso('Quantidade atualizada com sucesso!');
      setQuantidades((prevQuantidades) => ({
        ...prevQuantidades,
        [produtoId]: quantidade,
      }));
    } catch (error) {
      mensagemErro('Erro ao atualizar quantidade do produto');
    }
  };

  useEffect(() => {
    if (produtos.length) {
      const fetchQuantidades = async () => {
        try {
          const quantidadesPromises = produtos.map((produto) => {
            return axios.get(`${produtosURL}/${produto.id}`);
          });
          const quantidadesResponses = await Promise.all(quantidadesPromises);
          const quantidadesMap = {};
          quantidadesResponses.forEach((response) => {
            const produto = response.data;
            quantidadesMap[produto.id] = produto.quantidade;
          });
          setQuantidades(quantidadesMap);
        } catch (error) {
          console.error('Erro ao obter quantidades do estoque:', error);
        }
      };
      fetchQuantidades();
    }
  }, [produtos]);

  if (!produtos.length) return null;

  return (
    <div className='container'>
      <Card title='Listagem de Estoque'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <table className='table table-hover'>
                <thead>
                  <tr>
                    <th scope='col'>Id</th>
                    <th scope='col'>Nome</th>
                    <th scope='col'>Descrição</th>
                    <th scope='col'>Preço</th>
                    <th scope='col'>Quantidade</th>
                  </tr>
                </thead>
                <tbody>
                  {produtos.map((produto) => (
                    <tr key={produto.id}>
                      <td>{produto.id}</td>
                      <td>{produto.nome}</td>
                      <td>{produto.descricao}</td>
                      <td>{produto.preco}</td>
                      <td>
                        <input
                          type='number'
                          value={quantidades[produto.id] || ''}
                          onChange={(e) => atualizarQuantidade(produto.id, parseInt(e.target.value))}
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default ListagemEstoque;
