import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Stack from '@mui/material/Stack';
import Card from '../components/card';
import FormGroup from '../components/form-group';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import '../custom.css';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

function CadastroPedido() {
  const { idParam } = useParams();
  const navigate = useNavigate();
  const baseURL = `${BASE_URL}/pedidos`;

  const [id, setId] = useState('');
  const [clienteId, setClienteId] = useState('');
  const [produtos, setProdutos] = useState([]);
  const [status, setStatus] = useState('');
  const [dados, setDados] = useState(null);

  useEffect(() => {
    async function buscarDados() {
      if (idParam) {
        try {
          const response = await axios.get(`${baseURL}/${idParam}`);
          setDados(response.data);
        } catch (error) {
          mensagemErro(error.response.data);
        }
      }
    }
    buscarDados();
  }, [idParam]);

  useEffect(() => {
    if (dados) {
      setId(dados.id);
      setClienteId(dados.cliente.id);
      setProdutos(dados.produtos);
      setStatus(dados.status);
    }
  }, [dados]);

  useEffect(() => {
    async function buscarProdutos() {
      try {
        const response = await axios.get(`${BASE_URL}/produtos`);
        setProdutos(response.data);
      } catch (error) {
        mensagemErro('Erro ao buscar os produtos');
      }
    }
    buscarProdutos();
  }, []);

  function inicializar() {
    setId('');
    setClienteId('');
    setProdutos([]);
    setStatus('');
  }

  async function salvar() {
    if (clienteId.trim() === '') {
      mensagemErro('O campo Cliente é obrigatório.');
      return;
    }

    if (produtos.length === 0) {
      mensagemErro('Adicione pelo menos um produto ao pedido.');
      return;
    }

    if (status.trim() === '') {
      mensagemErro('O campo Status é obrigatório.');
      return;
    }

    const data = { id, clienteId, produtos, status };
    try {
      if (idParam) {
        await axios.put(`${baseURL}/${idParam}`, data, {
          headers: { 'Content-Type': 'application/json' },
        });
        mensagemSucesso(`Pedido ${id} alterado com sucesso!`);
      } else {
        await axios.post(baseURL, data, {
          headers: { 'Content-Type': 'application/json' },
        });
        mensagemSucesso(`Pedido ${id} cadastrado com sucesso!`);
      }
      navigate('/listagem-pedidos');
    } catch (error) {
      if (error.response) {
        mensagemErro(error.response.data);
      } else {
        mensagemErro('Ocorreu um erro de rede.');
      }
    }
  }

  const handleStatusChange = (event) => {
    setStatus(event.target.value);
  };

  const handleProdutoChange = (event) => {
    const selectedProducts = Array.from(event.target.selectedOptions, (option) => option.value);
    setProdutos(selectedProducts);
  };

  return (
    <div className='container'>
      <Card title='Cadastro de Pedido'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <FormGroup label='Cliente:' htmlFor='inputCliente'>
                <input
                  type='text'
                  id='inputCliente'
                  value={clienteId}
                  className='form-control'
                  name='cliente'
                  onChange={(e) => setClienteId(e.target.value)}
                />
              </FormGroup>

              <FormGroup label='Produtos:' htmlFor='selectProdutos'>
                <select
                  multiple
                  id='selectProdutos'
                  value={produtos}
                  className='form-control'
                  name='produtos'
                  onChange={handleProdutoChange}
                >
                  {produtos.map((produto) => (
                    <option key={produto.id} value={produto.id}>
                      {produto.nome}
                    </option>
                  ))}
                </select>
              </FormGroup>

              <FormGroup label='Status:' htmlFor='inputStatus'>
                <select
                  id='inputStatus'
                  value={status}
                  className='form-control'
                  name='status'
                  onChange={handleStatusChange}
                >
                  <option value='Em andamento'>Em andamento</option>
                  <option value='Concluído'>Concluído</option>
                  <option value='Cancelado'>Cancelado</option>
                </select>
              </FormGroup>

              <Stack spacing={1} padding={1} direction='row'>
                <button
                  onClick={salvar}
                  type='button'
                  className='btn btn-success'
                >
                  Salvar
                </button>
                <button
                  onClick={inicializar}
                  type='button'
                  className='btn btn-danger'
                >
                  Cancelar
                </button>
              </Stack>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default CadastroPedido;
