import React, { useState, useEffect } from 'react';
import Card from '../components/card';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import '../custom.css';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

const produtosURL = `${BASE_URL}/produtos`;
const carrinhosURL = `${BASE_URL}/carrinhos`;

function ListagemEstoque() {
  const [produtos, setProdutos] = useState([]);
  const [quantidades, setQuantidades] = useState({});

  useEffect(() => {
    axios.get(produtosURL).then((response) => {
      setProdutos(response.data);
    });
  }, []);

  useEffect(() => {
    const fetchQuantidades = async () => {
      try {
        const response = await axios.get(`${carrinhosURL}/quantidades`);
        const quantidadesMap = response.data.reduce((map, item) => {
          map[item.produtoId] = item.quantidade;
          return map;
        }, {});
        setQuantidades(quantidadesMap);
      } catch (error) {
        console.error('Erro ao obter quantidades do estoque:', error);
      }
    };
    fetchQuantidades();
  }, []);

  if (!produtos.length) return null;

  return (
    <div className='container'>
      <Card title='Listagem de Estoque'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <table className='table table-hover'>
                <thead>
                  <tr>
                    <th scope='col'>Id</th>
                    <th scope='col'>Nome</th>
                    <th scope='col'>Descrição</th>
                    <th scope='col'>Preço</th>
                    <th scope='col'>Quantidade</th>
                  </tr>
                </thead>
                <tbody>
                  {produtos.map((produto) => (
                    <tr key={produto.id}>
                      <td>{produto.id}</td>
                      <td>{produto.nome}</td>
                      <td>{produto.descricao}</td>
                      <td>{produto.preco}</td>
                      <td>{quantidades[produto.id] || 0}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default ListagemEstoque;
