import React, { useState, useEffect } from 'react';
import Card from '../components/card';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import '../custom.css';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

const estoqueURL = `${BASE_URL}/estoques`;

function ListagemEstoque() {
  const [dados, setDados] = useState(null);

  useEffect(() => {
    axios.get(estoqueURL).then((response) => {
      setDados(response.data);
    });
  }, []);

  const atualizarQuantidade = async (id, quantidade) => {
    try {
      await axios.put(`${estoqueURL}/${id}`, { quantidade });
      mensagemSucesso('Quantidade atualizada com sucesso!');
      setDados((dados) =>
        dados.map((produto) => {
          if (produto.id === id) {
            return { ...produto, quantidade };
          }
          return produto;
        })
      );
    } catch (error) {
      mensagemErro('Erro ao atualizar quantidade do produto');
    }
  };

  if (!dados) return null;

  return (
    <div className='container'>
      <Card title='Listagem de Estoque'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <table className='table table-hover'>
                <thead>
                  <tr>
                    <th scope='col'>Id</th>
                    <th scope='col'>Nome</th>
                    <th scope='col'>Descrição</th>
                    <th scope='col'>Preço</th>
                    <th scope='col'>Quantidade</th>
                    <th scope='col'>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {dados.map((produto) => (
                    <tr key={produto.id}>
                      <td>{produto.id}</td>
                      <td>{produto.nome}</td>
                      <td>{produto.descricao}</td>
                      <td>{produto.preco}</td>
                      <td>
                        <input
                          type='number'
                          value={produto.quantidade}
                          onChange={(e) =>
                            atualizarQuantidade(
                              produto.id,
                              parseInt(e.target.value)
                            )
                          }
                        />
                      </td>
                      <td>
                        <button
                          className='btn btn-danger'
                          onClick={() => atualizarQuantidade(produto.id, 0)}
                        >
                          Zerar Estoque
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default ListagemEstoque;
