import React, { useState, useEffect } from 'react';
import Card from '../components/card';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import '../custom.css';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

const produtosURL = `${BASE_URL}/produtos`;
const carrinhosURL = `${BASE_URL}/carrinhos`;

function ListagemEstoque() {
  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    axios.get(produtosURL).then((response) => {
      setProdutos(response.data);
    });
  }, []);

  useEffect(() => {
    const fetchQuantidades = async () => {
      try {
        const response = await axios.get(carrinhosURL);
        const carrinhos = response.data;

        const quantidadesMap = {};

        carrinhos.forEach((carrinho) => {
          carrinho.produtos.forEach((produtoCarrinho) => {
            const { id, quantidade } = produtoCarrinho.produto;
            if (!quantidadesMap[id]) {
              quantidadesMap[id] = 0;
            }
            quantidadesMap[id] += quantidade;
          });
        });

        const produtosAtualizados = produtos.map((produto) => {
          const { id, nome } = produto;
          const quantidade = quantidadesMap[id] || 0;
          return {
            id,
            nome,
            quantidade,
          };
        });

        setProdutos(produtosAtualizados);
      } catch (error) {
        console.error('Erro ao obter quantidades do estoque:', error);
      }
    };

    if (produtos.length > 0) {
      fetchQuantidades();
    }
  }, [produtos]);

  if (!produtos.length) return null;

  return (
    <div className='container'>
      <Card title='Listagem de Estoque'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <table className='table table-hover'>
                <thead>
                  <tr>
                    <th scope='col'>Nome</th>
                    <th scope='col'>Quantidade</th>
                  </tr>
                </thead>
                <tbody>
                  {produtos.map((produto) => (
                    <tr key={produto.id}>
                      <td>{produto.nome}</td>
                      <td>{produto.quantidade}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default ListagemEstoque;
