import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Stack from '@mui/material/Stack';
import Card from '../components/card';
import FormGroup from '../components/form-group';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import '../custom.css';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

function CadastroProduto() {
  const { idParam } = useParams();
  const navigate = useNavigate();
  const baseURL = `${BASE_URL}/produtos`;

  const [id, setId] = useState('');
  const [clienteId, setClienteId] = useState('');
  const [produtos, setProdutos] = useState([]);
  const [precoTotal, setPrecoTotal] = useState(0);
  const [dados, setDados] = useState(null);

  useEffect(() => {
    async function buscarDados() {
      if (idParam) {
        try {
          const response = await axios.get(`${baseURL}/${idParam}`);
          setDados(response.data);
        } catch (error) {
          mensagemErro(error.response.data);
        }
      }
    }
    buscarDados();
  }, [idParam]);

  useEffect(() => {
    if (dados) {
      setId(dados.id);
      setClienteId(dados.cliente.id);
      setProdutos(dados.produtos);
      setPrecoTotal(dados.precoTotal);
    }
  }, [dados]);

  function inicializar() {
    setId('');
    setClienteId('');
    setProdutos([]);
    setPrecoTotal(0);
  }

  async function salvar() {
    const data = { id, cliente: { id: clienteId }, produtos, precoTotal };
    try {
      if (idParam) {
        await axios.put(`${baseURL}/${idParam}`, data, {
          headers: { 'Content-Type': 'application/json' },
        });
        mensagemSucesso(`Produto alterado com sucesso!`);
      } else {
        await axios.post(baseURL, data, {
          headers: { 'Content-Type': 'application/json' },
        });
        mensagemSucesso(`Produto cadastrado com sucesso!`);
      }
      navigate(`/listagem-produtos`);
    } catch (error) {
      if (error.response) {
        mensagemErro(error.response.data);
      } else {
        mensagemErro('Ocorreu um erro de rede.');
      }
    }
  }

  return (
    <div className='container'>
      <Card title='Cadastro de Produto'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <FormGroup label='Cliente Id:' htmlFor='inputClienteId'>
                <input
                  type='text'
                  id='inputClienteId'
                  value={clienteId}
                  className='form-control'
                  name='clienteId'
                  onChange={(e) => setClienteId(e.target.value)}
                />
              </FormGroup>

              {/* Aqui você pode adicionar os campos para adicionar/remover produtos */}
              {/* por exemplo, utilizando um componente de seleção de produtos */}

              <FormGroup label='Preço Total:' htmlFor='inputPrecoTotal'>
                <input
                  type='number'
                  id='inputPrecoTotal'
                  value={precoTotal}
                  className='form-control'
                  name='precoTotal'
                  onChange={(e) => setPrecoTotal(e.target.value)}
                />
              </FormGroup>

              <Stack spacing={1} padding={1} direction='row'>
                <button
                  onClick={salvar}
                  type='button'
                  className='btn btn-success'
                >
                  Salvar
                </button>
                <button
                  onClick={inicializar}
                  type='button'
                  className='btn btn-danger'
                >
                  Cancelar
                </button>
              </Stack>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default CadastroProduto;
