import React, { useState, useEffect } from 'react';
import Card from '../components/card';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import '../custom.css';
import axios from 'axios';
import { BASE_URL } from '../config/axios';

const produtosURL = `${BASE_URL}/produtos`;
const carrinhosURL = `${BASE_URL}/carrinhos`;

function ListagemEstoque() {
  const [produtos, setProdutos] = useState([]);
  const [quantidades, setQuantidades] = useState({});

  useEffect(() => {
    axios.get(produtosURL).then((response) => {
      setProdutos(response.data);
    });
  }, []);

  const atualizarQuantidade = async (produtoId, quantidade) => {
    try {
      await axios.put(`${produtosURL}/${produtoId}`, { quantidade });
      mensagemSucesso('Quantidade atualizada com sucesso!');
      setQuantidades((prevQuantidades) => ({
        ...prevQuantidades,
        [produtoId]: quantidade,
      }));
    } catch (error) {
      mensagemErro('Erro ao atualizar quantidade do produto');
    }
  };

  const atualizarQuantidadesNoCarrinho = async (carrinhoId, produtosCarrinho) => {
    try {
      await axios.put(`${carrinhosURL}/${carrinhoId}`, { produtos: produtosCarrinho });
      mensagemSucesso('Quantidades do carrinho atualizadas com sucesso!');
    } catch (error) {
      mensagemErro('Erro ao atualizar quantidades do carrinho');
    }
  };

  const handleQuantidadeChange = (produtoId, quantidade) => {
    setQuantidades((prevQuantidades) => ({
      ...prevQuantidades,
      [produtoId]: quantidade,
    }));
  };

  const adicionarProdutoNoCarrinho = async (produtoId, carrinhoId) => {
    try {
      const response = await axios.get(`${carrinhosURL}/${carrinhoId}`);
      const carrinho = response.data;
      const produtosCarrinho = carrinho.produtos || [];

      if (!produtosCarrinho.find((produto) => produto.id === produtoId)) {
        const produto = { id: produtoId, quantidade: quantidades[produtoId] || 0 };
        produtosCarrinho.push(produto);
        await atualizarQuantidadesNoCarrinho(carrinhoId, produtosCarrinho);
        mensagemSucesso('Produto adicionado ao carrinho com sucesso!');
      } else {
        mensagemErro('Produto já adicionado ao carrinho');
      }
    } catch (error) {
      mensagemErro('Erro ao adicionar produto ao carrinho');
    }
  };

  if (!produtos.length) return null;

  return (
    <div className='container'>
      <Card title='Listagem de Estoque'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <table className='table table-hover'>
                <thead>
                  <tr>
                    <th scope='col'>Id</th>
                    <th scope='col'>Nome</th>
                    <th scope='col'>Descrição</th>
                    <th scope='col'>Preço</th>
                    <th scope='col'>Quantidade</th>
                    <th scope='col'>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {produtos.map((produto) => (
                    <tr key={produto.id}>
                      <td>{produto.id}</td>
                      <td>{produto.nome}</td>
                      <td>{produto.descricao}</td>
                      <td>{produto.preco}</td>
                      <td>
                        <input
                          type='number'
                          value={quantidades[produto.id] || ''}
                          onChange={(e) => handleQuantidadeChange(produto.id, parseInt(e.target.value))}
                        />
                      </td>
                      <td>
                        <button
                          className='btn btn-success'
                          onClick={() => adicionarProdutoNoCarrinho(produto.id, carrinhoId)}
                        >
                          Adicionar ao Carrinho
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default ListagemEstoque;
