import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Card, Stack, IconButton } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const baseURL = `${BASE_URL}/carrinhos`;


function ListagemCarrinhos() {
  const navigate = useNavigate();

  const cadastrar = () => {
    navigate(`/cadastro-carrinho`);
  };

  const editar = (id) => {
    navigate(`/cadastro-carrinho/${id}`);
  };

  const [dados, setDados] = useState(null);

  async function excluir(id) {
    let url = `${baseURL}/${id}`;
    try {
      await axios.delete(url);
      mensagemSucesso(`Carrinho excluÃ­do com sucesso!`);
      setDados(dados.filter((dado) => dado.id !== id));
    } catch (error) {
      mensagemErro(`Erro ao excluir o carrinho`);
    }
  }

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(baseURL);
        setDados(response.data);
      } catch (error) {
        console.log(error);
      }
    }
    fetchData();
  }, []);

  if (!dados) return null;

  return (
    <div className='container'>
      <Card title='Listagem de Carrinhos'>
        <div className='row'>
          <div className='col-lg-12'>
            <div className='bs-component'>
              <button
                type='button'
                className='btn btn-warning'
                onClick={() => cadastrar()}
              >
                Novo Carrinho
              </button>
              <table className='table table-hover'>
                <thead>
                  <tr>
                    <th scope='col'>Id</th>
                    <th scope='col'>Cliente</th>
                    <th scope='col'>Produto</th>
                    <th scope='col'>AÃ§Ãµes</th>
                  </tr>
                </thead>
                <tbody>
                  {dados.map((dado) => (
                    <tr key={dado.id}>
                      <td>{dado.id}</td>
                      <td>{dado.cliente.nome}</td>
                      <td>
                        {dado.produtos.map((produto) => (
                          <span key={produto.id}>{produto.nome}, </span>
                        ))}
                      </td>
                      <td>{dado.status}</td>
                      <td>
                        <Stack spacing={1} padding={0} direction='row'>
                          <IconButton
                            aria-label='edit'
                            onClick={() => editar(dado.id)}
                          >
                            <EditIcon />
                          </IconButton>
                          <IconButton
                            aria-label='delete'
                            onClick={() => excluir(dado.id)}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Stack>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

export default ListagemCarrinhos;
