import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { mensagemSucesso, mensagemErro } from '../components/toastr';
import { BASE_URL } from '../config/axios';

function ListagemPedidos() {
  const [pedidos, setPedidos] = useState([]);

  useEffect(() => {
    carregarPedidos();
  }, []);

  async function carregarPedidos() {
    try {
      const response = await axios.get(`${BASE_URL}/pedidos`);
      setPedidos(response.data);
    } catch (error) {
      mensagemErro('Ocorreu um erro ao carregar os pedidos.');
    }
  }

  async function concluirPedido(pedidoId) {
    try {
      const response = await axios.put(
        `${BASE_URL}/pedidos/${pedidoId}`,
        { status: 'Concluído' },
        {
          headers: { 'Content-Type': 'application/json' },
        }
      );
      const pedidoAtualizado = response.data;
      setPedidos((prevPedidos) => {
        const index = prevPedidos.findIndex((pedido) => pedido.id === pedidoId);
        if (index !== -1) {
          const updatedPedidos = [...prevPedidos];
          updatedPedidos[index] = pedidoAtualizado;
          return updatedPedidos;
        }
        return prevPedidos;
      });
      mensagemSucesso('Pedido concluído com sucesso!');
    } catch (error) {
      mensagemErro('Ocorreu um erro ao concluir o pedido.');
    }
  }

  return (
    <div className='container'>
      <h1>Listagem de Pedidos</h1>
      <table className='table table-striped'>
        <thead>
          <tr>
            <th>ID</th>
            <th>Cliente</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {pedidos.map((pedido) => (
            <tr key={pedido.id}>
              <td>{pedido.id}</td>
              <td>{pedido.cliente}</td>
              <td>{pedido.status}</td>
              <td>
                {pedido.status !== 'Concluído' && (
                  <button
                    className='btn btn-primary'
                    onClick={() => concluirPedido(pedido.id)}
                  >
                    Concluir
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListagemPedidos;
